require.config({
  paths: {
    'bundle': '$services.webjars.url('org.xwiki.contrib:webpack-demo-v8', 'bundle.js')'
  }
});

require(['jquery', 'bundle'], function($, bundle) {

  var solrServiceURL = new XWiki.Document('SuggestSolrService', 'XWiki').getURL('get');
  var data = null;
  var nodeKeyCounter = 1;
  var edgeKeyCounter = 1;

  $.post(solrServiceURL, {
    outputSyntax: 'plain',
    nb: 100,
    media: 'json',
    query: [
      'q=*:*',
      'q.op=AND',
      'fq=type:DOCUMENT',
      'fl=title_,reference,links'
    ].join('\n'),
    input: " "
  }, function(response) {
    data = response;

    var nodes = data.map(function(obj) {
      var referenceArray = XWiki.Model.resolve(obj.reference);
      var url = new XWiki.Document(referenceArray).getURL();

      var node = {
        key: obj.reference,
        attributes: {
          label: obj.title_,
          color: getRandomColor(),
          reference: obj.reference,
          PageURL: url
        }
      };
      return node;
    });

    var edges = [];
    data.forEach(function(obj) {
      if (obj.links && obj.links.length > 0) {
        obj.links.forEach(function(link) {
          var target = link.replace(/^entity:/, '');
          var isValid = data.some(function(item) {
            return item.reference === target;
          });
          if (isValid) {
            edges.push({
              key: edgeKeyCounter.toString(),
              target: target,
              source: obj.reference
            });
            edgeKeyCounter++;
          }
        });
      }
    });
    var myPersonalData = {
      nodes: nodes,
      edges: edges
    };

  // Function to generate a random hex color code
    function getRandomColor() {
      var letters = '0123456789ABCDEF';
      var color = '#';
      for (var i = 0; i < 6; i++) {
        color += letters[Math.floor(Math.random() * 16)];
      }
      return color;
    }

    console.log("This is the modified data:", myPersonalData);
    bundle.visualize(myPersonalData, "sigma-container");


    // THIS IS FOR DEBUGGING PURPOSE, THE JSON FILE IS DIRECTLY OPENED IN THE NEW TAB SO I DONT HAVE TO OPEN CONSOLE AGAIN AND AGAIN
    // Convert the output to a JSON string
    var jsonData = JSON.stringify(myPersonalData, null, 2);

    // Create a Blob object from the JSON string
    var blob = new Blob([jsonData], { type: 'application/json' });

    // Create a URL for the Blob object
    var urlBlob = URL.createObjectURL(blob);

    // Open the URL in a new tab/window
    window.open(urlBlob);
  });
});

